import React, { Component, useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const DetailView = () => {
  let params = useParams();

  const [posts, setPosts] = useState([]);

  useEffect(() => {
      setPosts(params.data);
  }, [params]);

  return(
    <div>
        <p>{posts.content}</p>
    </div>
  ); 
};

export default DetailView;
